	final URL url = new URL("http://htmlunit.sourceforge.net");
	final HtmlPage page = (HtmlPage)webClient.getPage(url);
	assertEquals( "Welcome to HtmlUnit", page.getTitleText();
}

public void testHomePage() throws Exception {
	final WebClient webClient = new WebClient(BrowserVersion.MOZILLA_1_0, "http://myproxyserver", 8000);
	final URL url = new URL("http://htmlunit.sourceforge.net");
	final HtmlPage page = (HtmlPage)webClient.getPage(url);
	assertEquals( "Welcome to HtmlUnit", page.getTitleText();
}

public void testHomePage() throws Exception {
	final WebClient webClient = new WebClient();
	final URL url = new URL("http://htmlunit.sourceforge.net");

	// Get the first page
	final HtmlPage page1 = (HtmlPage)webClient.getPage(url);

	// Get the form that we are dealing with and within that form, find the submit button
	// and the field that we want to change.
	final HtmlForm form = page1.getFormByName("myform");
	final HtmlSubmitInput button = (HtmlSubmitInput)form.getInputByName("submitbutton");
	final HtmlTextInput textField = (HtmlTextInput)form.getInputByName("userid");

	// Change the value of the text field
	textField.setValueAttribute("root");

	// Now submit the form by clicking the button and get back the second page.
	final HtmlPage page2 = (HtmlPage)button.click();
}
